import React, { useEffect, useCallback, useState } from 'react';
import 'regenerator-runtime';
import { createSpeechlySpeechRecognition } from '@speechly/speech-recognition-polyfill';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import axios from 'axios';
import { Microphone, MicrophoneOff, Trash } from 'tabler-icons-react';
import Scene from '../components/SceneWithAr';
import LoadingScreen from '../components/LoadingScreen';
const appId = '01403d73-5ac1-47b5-a2aa-2059ec480d13';
const SpeechlySpeechRecognition = createSpeechlySpeechRecognition(appId);
SpeechRecognition.applyPolyfill(SpeechlySpeechRecognition);

const DallE = () => {
	const [isLoading, setIsLoading] = useState(false);
	const {
		transcript,
		listening,
		resetTranscript,
		browserSupportsSpeechRecognition,
		finalTranscript,
	} = useSpeechRecognition();
	const handleListen = () => {
		if (listening) {
			SpeechRecognition.stopListening();
		} else {
			SpeechRecognition.startListening({ continuous: true });
		}
	};

	const [apiAnswer, setApiAnswer] = useState('');
	const handleSubmit = async (question) => {
		setIsLoading(true);
		await axios
			.post('/api/v1/dall-e', {
				message: question,
				userId: 1,
			})
			.then((res) => {
				setApiAnswer(res.data.images);
				setIsLoading(false);
			});

		resetTranscript();
	};

	const defaultCommands = [
		'cars and trucks and airplanes war',
		'red bear walking on the grass',
		'ducks fighting in the sky',
	];

	if (typeof window !== 'undefined' && !browserSupportsSpeechRecognition) {
		return <span>Browser doesn't support speech recognition.</span>;
	}

	return (
		<div className='w-full h-full flex flex-row '>
			<Scene image={apiAnswer} />
			<div className='flex flex-col items-center justify-center bg- h-screen mx-auto gap-4'>
				<ul className=' flex items-center w-[40rem]  py-10  gap-2 flex flex-col '>
					<span className='text-lg mb-2 w-full'>Example Commands:</span>

					{defaultCommands.map((command, index) => (
						<li
							key={index}
							onClick={() => handleSubmit(command)}
							className='bg-blue-400 text-yellow-100 p-2 rounded-lg cursor-pointer w-full hover:bg-blue-700'
						>
							{command}
						</li>
					))}
				</ul>
				<div className='w-full flex flex-col items-center'>
					<div className='flex items-center relative flex-col '>
						<span className='text-lg mb-2 w-full'>Speak into the microphone:</span>
						<textarea
							defaultValue={transcript}
							onChange={(e) => {}}
							disabled
							id='speech-input'
							className='w-[40rem] h-64 rounded-lg border-gray-400 border-2 px-4 py-2 mb-4 disabled:bg-white'
						></textarea>
						<button
							className='absolute top-12 right-4 bg-teal-600 p-2 rounded-md '
							onClick={resetTranscript}
						>
							<Trash size={20} color={'#ffffff'} />
						</button>
					</div>

					<div className='w-[40rem] flex flex-row justify-end  gap-5'>
						<button
							onClick={handleListen}
							id='start-recognition'
							className='bg-blue-700 hover:bg-blue-800 text-white font-bold py-2 px-6 rounded flex flex-row items-center gap-4'
						>
							{listening ? (
								<MicrophoneOff size={20} strokeWidth={2} color={'#f5f5f5'} />
							) : (
								<Microphone size={20} strokeWidth={2} color={'#f5f5f5'} />
							)}

							{listening ? 'Stop Listening' : 'Start Listening'}
						</button>
						<button
							onClick={() => handleSubmit(finalTranscript)}
							id='start-recognition'
							className=' bg-slate-500 hover:bg-slate-700 text-white font-bold py-2 px-6 rounded '
						>
							Submit Transcript
						</button>
					</div>
				</div>
			</div>
			<LoadingScreen isLoading={isLoading} />
		</div>
	);
};

export default DallE;
